INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120887, 'Itraconazole', 'Imipramine', 'Moderate', 'The coadministration with azole antifungal agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). Elevated TCA levels may result in CNS toxicity, anticholinergic symptoms, and QT interval prolongation. The mechanism is decreased TCA clearance due to azole-related inhibition of the CYP450 3A4 isoenzyme, which is partially responsible for the metabolism of TCAs like amitriptyline, clomipramine, imipramine, and possibly others.', NULL, 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary.', NULL, 'During concomitant therapy with azole antifungal agents, patients stabilized on their TCA regimen should be observed for increased pharmacologic response to the TCA, and the dosage adjusted accordingly if necessary. Serum TCA levels should be monitored following initiation or discontinuation of azole antifungal therapy. Patients should be advised to notify their caregiver if they experience excessive dry mouth, visual disturbances, urinary retention, dizziness, syncope, fast or irregular heartbeat, constipation, or seizures.', 'Metabolism', 'Phenelzine, Duloxetine, Desipramine, Tranylcypromine, Trimipramine, Desvenlafaxine, Maprotiline, Fluoxetine, St. John''s Wort, Isocarboxazid, Oxitriptan, More', 'Caspofungin, Micafungin, Amphotericin B, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121025/', '[1] Dorsey ST, Biblio L