INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120890, 'Itraconazole', 'Irinotecan (liposomal)', 'Major', 'Coadministration with certain azole antifungal agents may significantly increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 is the isoenzyme responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', NULL, 'Concomitant use of irinotecan with ketoconazole or itraconazole is considered contraindicated.', NULL, 'Concomitant use of irinotecan with ketoconazole or itraconazole is considered contraindicated. Ketoconazole should be discontinued for at least one week before initiation of treatment with irinotecan, and itraconazole should be discontinued for up to two weeks before. Some authorities consider concomitant administration of irinotecan to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Topotecan, Teniposide', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121028/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals, Cambridge, MA.[3] Phansalker S, Desai AA, Bell D, et al "High-priority drug-drug interactions