INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120891, 'Itraconazole', 'Isoniazid', 'Moderate', 'The concomitant administration of potent CYP450 3A4 inducers may significantly reduce itraconazole and ketoconazole serum levels. The mechanism appears to be enhanced first-pass metabolism and hepatic enzyme metabolism (CYP450 3A4) of the antifungal agents. Antifungal therapy may fail.', NULL, 'Coadministration of potent CYP450 3A4 inducers with itraconazole or ketoconazole should be avoided.', NULL, 'Coadministration of potent CYP450 3A4 inducers with itraconazole or ketoconazole should be avoided. Some authorities recommend avoiding concomitant therapy with potent CYP450 3A4 inducers from 2 weeks before and during treatment with itraconazole. Fluconazole is eliminated unchanged by the kidneys and may be a more appropriate antifungal agent, depending on the patient''s condition.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Micafungin, Caspofungin, Miconazole, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121029/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nizoral (ketoconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.', 1767369485);
