INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120924, 'Itraconazole', 'Mephenytoin', 'Moderate', 'Phenytoin and other hydantoins may reduce itraconazole serum levels by up to 90%. The mechanism appears to be enhanced first-pass metabolism and hepatic enzyme metabolism (CYP450 3A4) of itraconazole. Itraconazole therapy may fail. Ketoconazole is affected in a similar manner. In addition, itraconazole may increase the plasma concentrations of phenytoin.', NULL, 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole.', NULL, 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole. Some experts suggest that if concomitant use is unavoidable, a dosage increase for itraconazole or ketoconazole should be considered. In addition, monitoring of phenytoin concentrations is recommended and the dosage adjusted if needed. Fluconazole is eliminated unchanged by the kidneys and may be a more appropriate antifungal agent, depending on the patient''s condition.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Paramethadione, Methsuximide, Fenfluramine, Eslicarbazepine, Lamotrigine, Phensuximide, Methylphenobarbital, Phenacemide, More', 'Caspofungin, Micafungin, Amphotericin B, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121062/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceuti