INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120981, 'Itraconazole', 'Pralsetinib', 'Major', 'Coadministration with a combined P-glycoprotein (P-gp) and potent CYP450 3A inhibitor may significantly increase the plasma concentrations of pralsetinib, which is primarily metabolized by the CYP450 3A isoenzyme. In vitro studies have shown pralsetinib is a time-dependent inhibitor of CYP450 3A4/5 isoenzymes and a P-gp inhibitor at clinically relevant concentrations.', NULL, 'Concomitant use of pralsetinib with a combined P-gp and potent CYP450 3A inhibitor should be avoided when possible.', NULL, 'Concomitant use of pralsetinib with a combined P-gp and potent CYP450 3A inhibitor should be avoided when possible. Following discontinuation of the combined P-gp and potent CYP450 3A inhibitor, and after an appropriate washout period (3 to 5 elimination half-lives), the pralsetinib dose taken prior to initiating the combined P-gp and potent CYP450 3A4 inhibitor may be resumed.', 'Metabolism', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Alpelisib, Lenvatinib, Trametinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121119/', '[1] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[3] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767369485);
