INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (120994, 'Itraconazole', 'Ranitidine (bismuth citrate)', 'Moderate', 'INTERVAL: Oral H2 antagonists may significantly decrease the plasma concentration of oral itraconazole capsules. The mechanism is related to an increase in gastric pH resulting in a decrease in absorption of the antifungal. Clinical monitoring of patient response is recommended.', NULL, 'H2 antagonists should be taken at least 2 hours after itraconazole.', NULL, 'H2 antagonists should be taken at least 2 hours after itraconazole. If necessary, an acidic pH may be produced by administration with an acidic beverage such as Coca-Cola(R) or Pepsi(R). However, clinicians should still consider the possibility of a reduced or subtherapeutic antifungal effect. It may be appropriate to switch to itraconazole oral solution or an agent like fluconazole or terbinafine whose absorption is not affected by stomach pH.', 'Absorption', 'Fluconazole, Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin, Voriconazole', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121132/', '[1] Blum RA, D''Andrea DT, Florentino BM, et al "Increased gastric pH and the bioavailability of fluconazole and ketoconazole." Ann Intern Med 114 (1991): 755-7[2] Katz HI "Drug interactions of the newer oral antifungal agents." Br J Dermatol 141 (1999): 26-32[3] "Product Information. Sporonox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Lazar JD, Wilner KD "Drug interactions with fluconazole." Rev Infect Dis 12 Suppl 3 (1990): s327-33[5] "Product Information. Sporanox oral solution (itraconazole)." Janssen Pharmaceutica, Titusv