INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121051, 'Itraconazole', 'Tipranavir', 'Moderate', 'Coadministration with azole antifungal agents may substantially increase the plasma concentrations of tipranavir administered in combination with low-dose ritonavir. The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of both tipranavir and ritonavir.', NULL, 'In patients treated with tipranavir/ritonavir, dosages of fluconazole, itraconazole, and ketoconazole exceeding 200 mg/day are not recommended.', NULL, 'In patients treated with tipranavir/ritonavir, dosages of fluconazole, itraconazole, and ketoconazole exceeding 200 mg/day are not recommended. Due to multiple enzymes involved with voriconazole metabolism, the interaction is difficult to predict and no recommendations are available.', 'Metabolism', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Maribavir, More', 'Micafungin, Caspofungin, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121189/', '[1] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] "