INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121695, 'Lorlatinib', 'Ixabepilone', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme. Subtherapeutic levels of ixabepilone may result.', NULL, 'The use of ixabepilone in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The use of ixabepilone in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ixabepilone is unknown. Whenever possible, therapeutic agents with low enzyme induction potential should be considered in patients treated with ixabepilone.', 'Metabolism', 'Idarubicin, Valrubicin, Plicamycin, Mitoxantrone, Bleomycin, Mitomycin, Daunorubicin, Dactinomycin, Epirubicin', 'Entrectinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121833/', '[1] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
