INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121698, 'Lyme disease vaccine (recombinant OspA)', 'Ixabepilone', 'Moderate', 'The administration of Lyme disease vaccine to immunosuppressed patients may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'Valrubicin, Plicamycin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121836/', '[1] "Product Information. Lymerix (Lyme disease vaccine)." SmithKline Beecham, Philadelphia, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121699, 'Meningococcal polysaccharide vaccine group C', 'Ixabepilone', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'Valrubicin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121837/', '[1] "Product Information. Imovax Rabies (rabies vaccine)." sanofi pasteur, Swiftwater, PA.[2] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations. Available from: URL: https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chap" ([2017 Aug]):[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] "Product Information. Havrix (hepatitis A vaccine)." SmithKline Beecham, Philadelphia, PA.[5] "Product Information. Fluzone Influenza Vaccine (influenza virus vaccine)." Connaught, Swiftwater, PA.[6] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence. Available from: URL: https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf." ([2017, Apr 17]):[7] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc., Lansing, MI.[8] "Product Information. Omnihib (haemophilus b conjugate vaccine)." SmithKline Beecham, Philadelphia, PA.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (121700, 'Methylphenobarbital', 'Ixabepilone', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme. Subtherapeutic levels of ixabepilone may result.', NULL, 'The use of ixabepilone in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The use of ixabepilone in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ixabepilone is unknown. Whenever possible, therapeutic agents with low enzyme induction potential should be considered in patients treated with ixabepilone.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Rufinamide, Clonazepam, More', 'Idarubicin, Valrubicin, Plicamycin, Mitoxantrone, Bleomycin, Mitomycin, Daunorubicin, Dactinomycin, Epirubicin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121838/', '[1] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
