INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60752, 'Cenobamate', 'Osilodrostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or 2B6 may decrease the plasma concentrations of osilodrostat, which is partially metabolized by these isoenzymes. According to the product labeling, multiple CYP450 isoenzymes (CYP450 3A4, 2B6, and 2D6) and UDP-glucuronosyltransferases contribute to osilodrostat metabolism, and no single pathway contributes greater than 25% to the total clearance. Some of the known CYP450 3A4 and 2B6 inducers also induce UDP-glucuronosyltransferases.', NULL, 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer.', NULL, 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer. An increase in the dosage of osilodrostat may be required. Dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations of their signs and symptoms. A reduction in dosage of osilodrostat may be required if the CYP450 3A4 and/or 2B6 inducer is discontinued during treatment with osilodrostat.', 'Metabolism', 'Ethotoin, Brivaracetam, Gabapentin, Valproic acid, Topiramate, Paramethadione, Methsuximide, Perampanel, Phensuximide, Lamotrigine, Phenacemide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60838/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc, Lebanon, NJ.[2] "Product