INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60959, 'Cerivastatin', 'Ceritinib', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9 and/or 3A4. In vitro data suggest that ceritinib may inhibit these isoenzymes at clinical concentrations.', NULL, 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include alfentanil, colchicine, cyclosporine, ergot derivatives, fentanyl, lovastatin, simvastatin, oral midazolam, triazolam, phenytoin, pimozide, quinidine, sildenafil, sirolimus, tacrolimus, and warfarin. If coadministration is required, dosage reductions for the CYP450 2C9 or 3A4 substrates should be considered.', 'Metabolism', 'Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Niacin, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61045/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yoovathaworn KC, Sriwatanakul K, Thithapandha A "Influence of caffeine on aspirin pharmacokinetics." Eur J Drug Metab Pharmacokinet 11 (1986): 71-6[4] "Product Information. Zykadia (ceritinib)."