INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60996, 'Dapsone', 'Ceritinib', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentration and the risk of adverse effects of drugs that are substrates of CYP450 2A6 and/or 2E1. The proposed mechanism, based on in vitro data, is decreased clearance due to ceritinib-mediated inhibition of CYP450 2A6 and 2E1.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of CYP450 2A6 and/or 2E1, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of CYP450 2A6 and/or 2E1, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ceritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Minocycline, Vitamin A, Isotretinoin, Fluorometholone, Azelaic acid, Adapalene', 'Entrectinib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61082/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[4] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60997, 'Darifenacin', 'Ceritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of darifenacin, which is partially metabolized by the isoenzyme. Darifenacin is also metabolized by CYP450 2D6, but 3A4 is the major pathway of metabolism in so-called poor metabolizers of 2D6.', NULL, 'The dosage of darifenacin should not exceed 7.', NULL, 'The dosage of darifenacin should not exceed 7.5 mg/day when used with potent CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of darifenacin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Phenyl salicylate, Oxybutynin, Ammonium chloride, Dimethyl sulfoxide', 'Acalabrutinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61083/', '[1] Niwa T, Shiraga T, Takagi A "Effect of antifungal drugs on cytochrome P450 (CYP) 2C9, CYP2C19, CYP3A4 activities in human liver microsomes." Biol Pharm Bull 28 (2005): 1805-8[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60998, 'Darolutamide', 'Ceritinib', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter. Caution is advised when darolutamide is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', NULL, '-', NULL, '-', 'Metabolism', 'Acalabrutinib, Dacomitinib, Avapritinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, Axitinib, More', 'Anastrozole, Exemestane, Fulvestrant', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61084/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (60999, 'Darunavir', 'Ceritinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ceritinib, which is a substrate of the isoenzyme. Because ceritinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death. Other, more common side effects such as diarrhea, nausea, vomiting, abdominal pain, hyperglycemia, and bradycardia may also increase.', NULL, 'Caution is advised if ceritinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if ceritinib is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to ceritinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the ceritinib dosage adjusted as necessary. Patients should have periodic ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 500 msec on at least two separate ECGs will require suspension of ceritinib therapy and immediate action to correct any concomitant risk factors before resuming treatment with a 150 mg dosage reduction. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Following discontinuation of the CYP450 3A4 inhibitor, ceritinib therapy should be resumed at the dosage that was taken prior to initiating the CYP450 3A4 inhibitor if an adjustment was made.', 'Metabolism', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Sunitinib, Futibatinib, Regorafenib, Imatinib, Alpelisib, Lenvatinib, Erdafitinib, Trametinib, Nintedanib, Vandetanib, Ribociclib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61085/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61000, 'Dasatinib', 'Ceritinib', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and pharmacologic effects of dasatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of dasatinib during and for 2 weeks after treatment with itraconazole. Close monitoring for toxicity (e.g., myelosuppression, bleeding complications, fluid retention, excessive slowing of heart rate or other conduction disturbances) and a reduction of dasatinib dosage to a range of 20 to 40 mg daily should be considered if there are no alternatives and concomitant use with these agents is necessary.', 'Metabolism', 'Trametinib, Nintedanib, Ceritinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 'Futibatinib, Alpelisib, Trametinib, Erdafitinib, Neratinib, Nintedanib, Dasatinib, Capmatinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61086/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Wohlt PD, Zheng L