INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360128, 'Amprenavir', 'Avapritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9. Increased exposure to avapritinib may increase the risk and/or severity of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils, and platelets.', 'DDInter', 1767317475, 'Concomitant use of avapritinib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Concomitant use of avapritinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360129, 'Anisindione', 'Avapritinib', 'Major', 'Coadministration of avapritinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317475, 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Patients should also seek immediate medical attention if they experience neurological signs and symptoms that may be associated with intracranial hemorrhage such as severe headache, vision problems, somnolence, or focal weakness. Brain imaging by magnetic resonance imaging (MRI) or computed tomography (CT) may be performed at the discretion of the physician based on severity and clinical presentation.', 'Synergism', 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
