INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360139, 'Bifidobacterium longum infantis', 'Avapritinib', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360140, 'Binimetinib', 'Avapritinib', 'Major', 'Coadministration of avapritinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317475, 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Patients should also seek immediate medical attention if they experience neurological signs and symptoms that may be associated with intracranial hemorrhage such as severe headache, vision problems, somnolence, or focal weakness. Brain imaging by magnetic resonance imaging (MRI) or computed tomography (CT) may be performed at the discretion of the physician based on severity and clinical presentation.', 'Synergism', 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
