INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360142, 'Boceprevir', 'Avapritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9. Increased exposure to avapritinib may increase the risk and/or severity of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils, and platelets.', 'DDInter', 1767317475, 'Concomitant use of avapritinib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Concomitant use of avapritinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
