INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360182, 'Dasatinib', 'Avapritinib', 'Major', 'Coadministration of dasatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with dasatinib is associated with severe thrombocytopenia.', 'DDInter', 1767317475, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for the first 2 months, then monthly thereafter or as clinically indicated. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360183, 'Deferasirox', 'Avapritinib', 'Major', 'Coadministration with drugs that have known ulcerogenic or hemorrhagic potential may increase the risk of gastrointestinal bleeding associated with the use of deferasirox. Fatal gastrointestinal hemorrhages have been reported during deferasirox therapy, especially in elderly patients with advanced hematologic malignancies and/or low platelet counts.', 'DDInter', 1767317475, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, hematemesis, anorexia, and/or black, tarry stools.', 'Synergism', 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, NULL, 'Major', 'pharmacodynamic');
