INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360193, 'Dextran (-1)', 'Avapritinib', 'Major', 'Coadministration of avapritinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317475, 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Patients should also seek immediate medical attention if they experience neurological signs and symptoms that may be associated with intracranial hemorrhage such as severe headache, vision problems, somnolence, or focal weakness. Brain imaging by magnetic resonance imaging (MRI) or computed tomography (CT) may be performed at the discretion of the physician based on severity and clinical presentation.', 'Synergism', 'Concomitant use of avapritinib with other drugs that increase the risk of bleeding should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
