INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360255, 'Inotersen', 'Avapritinib', 'Major', 'Coadministration of inotersen and drugs that interfere with platelet function or coagulation may potentiate the risk of serious, potentially life-threatening bleeding complications, including spontaneous intracranial and intrapulmonary hemorrhage. Inotersen causes reductions in platelet count that may result in sudden and unpredictable thrombocytopenia.', 'DDInter', 1767317475, 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding. A platelet count should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should not be administered in patients with a platelet count below 100 x 10^9/L or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of thrombocytopenia and to seek medical attention if they occur, including any unusual or prolonged bleeding (e.g., petechiae, easy bruising, hematoma, subconjunctival bleeding, gingival bleeding, epistaxis, hemoptysis, irregular or heavier than normal menstrual bleeding, hematemesis, hematuria, hematochezia, melena), neck stiffness, or atypical severe headache.', 'Synergism', 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding.', NULL, NULL, 'Major', 'pharmacodynamic');
