INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360305, 'Avapritinib', 'Niraparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with niraparib.', 'DDInter', 1767317475, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', 'Synergism', 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360306, 'Avapritinib', 'Ocrelizumab', 'Moderate', 'The use of the CD20-directed cytolytic antibody ocrelizumab with other immune-modulating or immunosuppressive therapy, including immunosuppressant doses of corticosteroids may increase the risk of infections.', 'DDInter', 1767317475, 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy. In addition, the prolonged immunosuppressant effects and mode of action of other immunosuppressant drugs such as daclizumab, fingolimod, natalizumab, teriflunomide, or mitoxantrone should be considered prior to starting ocrelizumab therapy. Patients should be advised to notify their doctor if they develop signs or symptoms of infection, including upper or lower respiratory tract infection, skin infection, herpes related infection, or PML.', 'Synergism', 'The increased risk of additive immunosuppression should be considered if coadministering ocrelizumab with other immunosuppressive therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
