INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360375, 'Suprofen (ophthalmic)', 'Avapritinib', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 1767317475, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360376, 'Suvorexant', 'Avapritinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', 'DDInter', 1767317475, 'Caution is advised when avapritinib is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils and platelets, and the dosing of avapritinib adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when avapritinib is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360377, 'Talimogene laherparepvec', 'Avapritinib', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360378, 'Tazemetostat', 'Avapritinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of avapritinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of avapritinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360379, 'Telaprevir', 'Avapritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9. Increased exposure to avapritinib may increase the risk and/or severity of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils, and platelets.', 'DDInter', 1767317475, 'Concomitant use of avapritinib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Concomitant use of avapritinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
