INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360422, 'Amprenavir', 'Avatrombopag', 'Moderate', 'Concomitant use of avatrombopag with strong CYP450 3A4/5 or CYP450 2C9 inhibitors may increase the systemic exposure (AUC) to avatrombopag. The increase in avatrombopag exposure is not expected to have a clinically important effect on platelet counts in patients with chronic liver disease scheduled to undergo a procedure due to the 5-day treatment duration.', 'DDInter', 1767317475, 'No dose adjustment is recommended; however, it may be advisable to monitor platelet count for signs of decreased efficacy of avatrombopag.', 'Metabolism', 'No dose adjustment is recommended; however, it may be advisable to monitor platelet count for signs of decreased efficacy of avatrombopag.', NULL, NULL, 'Moderate', 'pharmacodynamic');
