INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360424, 'Carbamazepine', 'Avatrombopag', 'Major', 'Concomitant use of avatrombopag with moderate to potent dual inducers of CYP450 2C9 and 3A4 may decrease the systemic exposure (AUC) of avatrombopag which may lead to reduced efficacy.', 'DDInter', 1767317475, 'For the treatment of patients with chronic immune thrombocytopenia, the manufacturer recommends an increased starting dose of avatrombopag 40 mg orally once daily when used concomitantly with a moderate to potent dual CYP450 2C9 and 3A4 inducer. In patients already receiving avatrombopag therapy and starting a moderate or strong dual CYP450 2C9 and 3A4 inducer, platelet counts should be monitored and avatrombopag dose adjusted according to the manufacturer''s product labeling as necessary. Dose adjustments are not recommended when prescribed for the treatment of thrombocytopenia in adult patients with chronic liver disease undergoing a procedure.', 'Metabolism', 'For the treatment of patients with chronic immune thrombocytopenia, the manufacturer recommends an increased starting dose of avatrombopag 40 mg orally once daily when used concomitantly with a moderate to potent dual CYP450 2C9 and 3A4 inducer.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360425, 'Carfilzomib', 'Avatrombopag', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of other agents also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis. Thromboprophylaxis should be considered according to local treatment protocols. Close monitoring for thromboembolic events is recommended in patients with known risk factors (e.g., prior thrombosis, hyperlipidemia, hypertension, smoking) for thromboembolism. Patients should be advised to seek immediate medical attention if they develop symptoms of thromboembolism, such as shortness of breath, chest pain, hemoptysis, or arm or leg swelling or pain.', 'Synergism', 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis.', NULL, NULL, 'Major', 'pharmacodynamic');
