INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360486, 'Ceritinib', 'Axitinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of axitinib during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Should concomitant treatment with a potent inhibitor be required, the manufacturer recommends that axitinib dosage be reduced by approximately one-half, as this is predicted to adjust the axitinib systemic exposure (AUC) to the range observed without inhibitors; however, clinical data are lacking. Subsequent dosage adjustments can be made according to individual therapeutic response and tolerability.', 'Metabolism', 'Concomitant use of axitinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360487, 'Chloramphenicol', 'Axitinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Otherwise, patients should be monitored closely for development of toxicity such as hypertension/hypertensive crisis, arterial and venous thromboembolic complications, hemorrhage, gastrointestinal perforation or fistula, thyroid dysfunction, reversible posterior leukoencephalopathy syndrome, proteinuria, and liver enzyme elevations or hepatic impairment.', 'Metabolism', 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
