INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360535, 'Axitinib', 'Lansoprazole', 'Minor', 'Since the aqueous solubility of axitinib is pH-dependent, with higher pH resulting in lower solubility, drugs that increase the pH of the upper gastrointestinal tract may reduce the oral bioavailability of axitinib. The interaction has been studied with rabeprazole. No dosage adjustment for axitinib is recommended during coadministration with proton pump inhibitors, H2-receptor antagonists, or antacids.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
