INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61011, 'Dexlansoprazole', 'Ceritinib', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of ceritinib and reduce its bioavailability. The interaction has not been formally studied; however, the solubility of ceritinib has been found to be pH-dependent in vitro and the drug becomes poorly soluble as pH increases.', NULL, 'Until more information is available, caution is advised if ceritinib is used with drugs that can alter the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids.', NULL, 'Until more information is available, caution is advised if ceritinib is used with drugs that can alter the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids. The potential for diminished therapeutic effects of ceritinib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61097/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
