INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61056, 'Esomeprazole', 'Ceritinib', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of ceritinib and reduce its bioavailability. The interaction has not been formally studied; however, the solubility of ceritinib has been found to be pH-dependent in vitro and the drug becomes poorly soluble as pH increases.', NULL, 'Until more information is available, caution is advised if ceritinib is used with drugs that can alter the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids.', NULL, 'Until more information is available, caution is advised if ceritinib is used with drugs that can alter the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids. The potential for diminished therapeutic effects of ceritinib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61142/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61057, 'Eszopiclone', 'Ceritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of both zopiclone and its pharmacologically active S(-) enantiomer, eszopiclone. Zopiclone has been shown in vitro to be metabolized by CYP450 3A4 and CYP450 2C8, while eszopiclone is primarily metabolized by CYP450 3A4 and 2E1 via demethylation and oxidation.', NULL, 'Due to increased risk of next-day psychomotor impairment, the dosages of eszopiclone and zopiclone should be reduced when used with potent CYP450 3A4 inhibitors.', NULL, 'Due to increased risk of next-day psychomotor impairment, the dosages of eszopiclone and zopiclone should be reduced when used with potent CYP450 3A4 inhibitors. The manufacturers recommend that total dose of eszopiclone not exceed 2 mg. An initial dose of 3.75 mg is recommended for zopiclone, which may be increased up to 5 mg if clinically indicated. Patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them, preferably at least 12 hours after administration of the hypnotic.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61143/', '[1] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[2] "Product Information. Imovane (zopiclone)." Rhone-Poulenc Rorer Canada Inc, Laval, IN.[3] Alderman CP, Gebauer MG, Gilbert AL, Condon JT "Possible interaction of zopiclone and nefazodone." Ann Pharmacother 35 (2001): 1378-80[4] Becquemont L, Mouaj