INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61133, 'Isradipine', 'Ceritinib', 'Major', 'Coadministration of ceritinib with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, digitalis, and clonidine may increase the risk of bradycardia, which can lead to heart block and QT prolongation.', NULL, 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible.', NULL, 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible. All patients treated with ceritinib should have their heart rate and blood pressure monitored regularly. Consult the product labeling for dosage adjustment and management recommendations if symptomatic or life-threatening bradycardia occurs. Patients should be advised to notify their doctor if they experience irregular heartbeat, slow pulse, dizziness, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61219/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[6] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin Pharmacol 56 (2000): 693-8[7] Pillai U, Muzaffar J, Sandeep S, Yancey A "Grapefruit 