INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61207, 'Ceritinib', 'Midostaurin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme. The increase in midostaurin concentrations may be particularly pronounced when CYP450 3A4 inhibitors are administered during the first week of midostaurin administration.', NULL, 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of midostaurin with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61293/', '[1] Dutreix C, Munarini F, Lorenzo S, Roesel J, Wang Y "Investigation into CYP3A4-mediated drug-drug interactions on midostaurin in healthy volunteers." Cancer Chemother Pharmacol 72 (2013): 1223-34[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61208, 'Ceritinib', 'Mifepristone', 'Major', 'Mifepristone may prolong the QTc interval in a dose-related manner. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Coadministration with potent inhibitors of CYP450 3A4 such as ceritinib, clarithromycin, ketoconazole, posaconazole, saquinavir, telithromycin, and voriconazole may significantly increase the plasma concentrations of mifepristone, which is primarily metabolized by the isoenzyme. Additionally, these agents may prolong the QT interval, which may potentiate the risk of cardiac arrhythmias when administered with mifepristone.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided. If concomitant use with ceritinib, clarithromycin, ketoconazole, posaconazole, saquinavir, telithromycin, or voriconazole is required and no alternatives are available, the dosage of mifepristone should be limited to 300 mg/day in accordance with labeling recommendation for use in combination with potent CYP450 3A4 inhibitors. Serum potassium should be assessed prior to starting mifepristone and 1 to 2 weeks following initiation of therapy or an increase in dosage, and periodically as needed. Hypokalemia must be corrected prior to initiation of mifepristone. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heart rhythm. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61294/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[3] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[4] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
