INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61258, 'Ceritinib', 'Panobinostat', 'Major', 'Panobinostat can cause dose-dependent prolongation of the QT interval. Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of panobinostat, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of panobinostat with potent CYP450 3A4 inhibitors that can also prolong the QT interval including ceritinib, clarithromycin, ketoconazole, posaconazole, saquinavir, telithromycin, and voriconazole should generally be avoided.', NULL, 'Concomitant use of panobinostat with potent CYP450 3A4 inhibitors that can also prolong the QT interval including ceritinib, clarithromycin, ketoconazole, posaconazole, saquinavir, telithromycin, and voriconazole should generally be avoided. If coadministration with these agents is required, the manufacturer recommends reducing the dose of panobinostat to 10 mg for the treatment of multiple myeloma, given in combination with bortezomib and dexamethasone. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting panobinostat therapy and periodically during treatment as clinically indicated. In the premarketing trial, ECGs were performed at baseline and prior to initiation of each cycle for the first 8 cycles. Panobinostat should not be started if baseline QTc is greater than 450 msec. Likewise, treatment should be interrupted in patients who develop QTc prolongation of 480 msec or greater until recovery to less than or equal to Grade 1, then resumed at a reduced dose. In case of recurrence, therapy should be withheld until recovery to less than or equal to Grade 1, then resumed at a further reduced dose if necessary. Permanently discontinue panobinostat therapy if Grade 3 or 4 QTc prolongation does not resolve.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61344/', '[1] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Hamberg P, Woo MM, Chen LC, et al. "Effect of ketoconazole-mediated CYP3A4 inhibition on clinical pharmacokinetics of panobinostat (LBH589), an orally active histone deacetylase inhibitor." Cancer Chemother Pharmacol 68 (2011): 805-13[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Farydak (panobinostat)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61259, 'Ceritinib', 'Pantoprazole', 'Moderate', 'Drugs that alter the pH of the upper gastrointestinal tract may affect the solubility of ceritinib and reduce its bioavailability. The interaction has not been formally studied; however, the solubility of ceritinib has been found to be pH-dependent in vitro and the drug becomes poorly soluble as pH increases.', NULL, 'Until more information is available, caution is advised if ceritinib is used with drugs that can alter the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids.', NULL, 'Until more information is available, caution is advised if ceritinib is used with drugs that can alter the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids. The potential for diminished therapeutic effects of ceritinib should be considered, and pharmacologic response should be closely monitored.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61345/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61260, 'Ceritinib', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61346/', '[1] Vrolix M, Piessens J, De Geest H "Torsades de pointes after intracoronary papaverine." Eur Heart J 12 (1991): 273-6[2] Jain A, Jenkins MG "Intracoronary electrocardiogram during torsade des pointes secondary to intracoronary papaverine." Cathet Cardiovasc Diagn 18 (1989): 255-7[3] Goto M, Sato M, Kitzazawa H, et.al "Papaverine-induced QT interval prolongation and ventricular fibrillation in a patient with a history of drug-induced QT prolongation." Intern Med 53 (2014): 1629-31[4] Talman CL, Winniford MD, Rossen JD, Simonetti I, Kienzle MG, Marcus ML "Polymorphous ventricular tachycardia: a side effect of intracoronary papaverine." J Am Coll Cardiol 15 (1990): 275-8[5] Kern MJ, Deligonul U, Serota H, Gudipati C, Buckingham T "Ventricular arrhythmia due to intracoronary papaverine: analysis of QT intervals and coronary vasodilatory reserve." Cathet Cardiovasc Diagn 19 (1990): 229-36[6] Nakayama M, Tanaka N, Sakoda K, et.al "Papaverine-induced polymorphic ventricular tachycardia during coronary flow reserve study of patients with moderate coronary artery disease." Circ J 79 (2015): 530-6[7] Inoue T, Asahi S, Takayanagi K, Morooka S, Takabatake Y "QT prolongation and possibility of ventricular arrhythmias after intracoronary papaverine." Cardiology 84 (1994): 9-13[8] Nakayama M, Saito A, Kitazawa H, et.al "Papaverine-induced polymorphic ventricular tachycardia in relation to QTU and giant T-U waves in four cases." Intern Med 51 (2012): 351-6', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61261, 'Ceritinib', 'Paricalcitol', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of paricalcitol, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors. Serum calcium and phosphorus levels should be monitored closely. If clinically significant hypercalcemia develops, the paricalcitol dose should be reduced or interrupted and support therapy administered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61347/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
