INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61279, 'Ceritinib', 'Pindolol', 'Major', 'Coadministration of ceritinib with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, digitalis, and clonidine may increase the risk of bradycardia, which can lead to heart block and QT prolongation.', NULL, 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible.', NULL, 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible. All patients treated with ceritinib should have their heart rate and blood pressure monitored regularly. Consult the product labeling for dosage adjustment and management recommendations if symptomatic or life-threatening bradycardia occurs. Patients should be advised to notify their doctor if they experience irregular heartbeat, slow pulse, dizziness, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61365/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[6] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin Pharmacol 56 (2000): 693-8[7] Pillai U, Muzaffar J, Sandeep S, Yancey A "Grapefruit juice and verapamil: a toxic cocktail." South Med J 102 (2009): 308-9[8] McAllister RG, Jr "Clinical pharmacology of slow channel blocking agents." Prog Cardiovasc Dis 25 (1982): 83-102[9] "Product Information. Covera-HS (verapamil)." Searle, Skokie, IL.[10] Fuhr U, Muller-Peltzer H, Kern R, et al. "Effects of grapefruit juice and smoking on verapamil concentrations in steady state." Eur J Clin Pharmacol 58 (2002): 45-53[11] Arayne MS, Sultana N, Bibi Z "Review: grape fruit juice - drug interactions." Pak J Pharm Sci 18 (2005): 45-57[12] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions. 1998." Br J Clin Pharmacol 58 (2004): S831-40; discussion S841-3[13] Zaidenstein R, Dishi V, Gips M, Soback S, Cohen N, Weissgarten J, Blatt A, Golik A "The effect of grapefruit juice on the pharmacokinetics of orally administered verapamil." Eur J Clin Pharmacol 54 (1998): 337-40', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61280, 'Ceritinib', 'Pirbuterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61366/', '[1] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Whyte KF, Addis GJ, Whitesmith R, Reid JL "The mechanism of salbutamol-induced hypokalaemia." Br J Clin Pharmacol 23 (1987): 65-71[3] Sanders JP, Potter DE, Ellis S, Bee DE, Grant JA "Metabolic and cardiovascular effects of carbuterol and metaproterenol." J Allergy Clin Immunol 60 (1977): 174-9[4]