INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61341, 'Ceritinib', 'Secobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ceritinib, which is a substrate of the isoenzyme.', NULL, 'Patients receiving ceritinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to ceritinib.', NULL, 'Patients receiving ceritinib with CYP450 3A4 inducers should be monitored for potentially diminished pharmacologic response to ceritinib. Concomitant use should be avoided if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61427/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61342, 'Ceritinib', 'Selpercatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme. Increased exposure to selpercatinib may increase the risk of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema, and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis).', NULL, 'Concomitant use of selpercatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selpercatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends decreasing the dosage of selpercatinib. Patients receiving selpercatinib 120 mg twice daily should have the dosage reduced to 40 mg twice daily, and those receiving 160 mg twice daily should have the dosage reduced to 80 mg twice daily. Close monitoring for adverse effects is advisable, including more frequent ECGs and laboratory monitoring of liver enzymes, bilirubin, electrolytes, glucose, and blood counts. Selpercatinib treatment should be discontinued, interrupted, or dosage reduced in patients with serious or life-threatening toxicities in accordance with the product labeling. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the selpercatinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61428/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61343, 'Ceritinib', 'Selumetinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3. When coadministered with itraconazole, a potent CYP450 3A4 inhibitor, selumetinib peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 19% and 49%, respectively.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, a reduction in the dosage of selumetinib is recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61429/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365204);
