INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61352, 'Ceritinib', 'Sirolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly increase the plasma concentrations of sirolimus following oral administration. Sirolimus is a substrate of both CYP450 3A4 isoenzyme and P-gp efflux transporter, thus their inhibition in the intestine can enhance the absorption of sirolimus.', NULL, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided. The manufacturers of posaconazole and voriconazole consider coadministration with sirolimus to be contraindicated. Some authorities recommend avoiding concomitant use of sirolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61438/', '[1] "Product Information. Noxafil (posaconazole)." Schering-Plough Corporation, Kenilworth, NJ.[2] Dodds-Ashley E "Management of drug and food interactions with azole antifungal agents in transplant recipients." Pharmacotherapy 30 (2010): 842-54[3] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Claesson K, Brattstrom C, Burke JT "Sirolimus and erythromycin interaction: two cases." Transplant Proc 33 (2001): 2136[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Floren LC, Christians U, Zimmerman JJ, et al "Sirolimus oral bioavailability increases ten-fold with concomitant ketoconazole." Clin Pharmacol Ther 65 (1999): 159[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[8] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[9] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[10] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365204);
