INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61367, 'Ceritinib', 'Sulfasalazine', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the intestinal P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters, such as dabigatran, rosuvastatin, sulfasalazine, and topotecan. The proposed mechanism, based on in vitro data, is decreased clearance due to ceritinib-mediated inhibition of intestinal P-gp and/or BCRP efflux transport proteins.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of the intestinal P-gp and/or BCRP transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of the intestinal P-gp and/or BCRP transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ceritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61453/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
