INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61383, 'Ceritinib', 'Temsirolimus', 'Major', 'Coadministration of temsirolimus with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of intravenous temsirolimus during and for 2 weeks after treatment with itraconazole (US). If coadministration is required, the manufacturer recommends reducing the temsirolimus dosage to 12.5 mg once a week. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the temsirolimus dosage is adjusted upward to the normally recommended dosage (i.e., 25 mg once a week) or the dosage used prior to initiation of the CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61469/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61384, 'Ceritinib', 'Teniposide', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9 and/or 3A4. In vitro data suggest that ceritinib may inhibit these isoenzymes at clinical concentrations.', NULL, 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include alfentanil, colchicine, cyclosporine, ergot derivatives, fentanyl, lovastatin, simvastatin, oral midazolam, triazolam, phenytoin, pimozide, quinidine, sildenafil, sirolimus, tacrolimus, and warfarin. If coadministration is required, dosage reductions for the CYP450 2C9 or 3A4 substrates should be considered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61470/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yoovathaworn KC, Sriwatanakul K, Thithapandha A "Influence of caffeine on aspirin pharmacokinetics." Eur J Drug Metab Pharmacokinet 11 (1986): 71-6[4] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[6] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 1767365204);
