INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61393, 'Ceritinib', 'Tiagabine', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9 and/or 3A4. In vitro data suggest that ceritinib may inhibit these isoenzymes at clinical concentrations.', NULL, 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, 'Concomitant use of ceritinib and sensitive CYP450 2C9/3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include alfentanil, colchicine, cyclosporine, ergot derivatives, fentanyl, lovastatin, simvastatin, oral midazolam, triazolam, phenytoin, pimozide, quinidine, sildenafil, sirolimus, tacrolimus, and warfarin. If coadministration is required, dosage reductions for the CYP450 2C9 or 3A4 substrates should be considered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61479/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yoovathaworn KC, Sriwatanakul K, Thithapandha A "Influence of caffeine on aspirin pharmacokinetics." Eur J Drug Metab Pharmacokinet 11 (1986): 71-6[4] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[6] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61394, 'Ceritinib', 'Ticagrelor', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities.', NULL, 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities. The U.S. labeling for ticagrelor recommends avoiding use with potent CYP450 3A4 inhibitors. Labeling for itraconazole, a potent CYP450 3A4 inhibitor, specifically contraindicates use with ticagrelor during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61480/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[4] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61395, 'Ceritinib', 'Timolol', 'Major', 'Coadministration of ceritinib with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, digitalis, and clonidine may increase the risk of bradycardia, which can lead to heart block and QT prolongation.', NULL, 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible.', NULL, 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible. All patients treated with ceritinib should have their heart rate and blood pressure monitored regularly. Consult the product labeling for dosage adjustment and management recommendations if symptomatic or life-threatening bradycardia occurs. Patients should be advised to notify their doctor if they experience irregular heartbeat, slow pulse, dizziness, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61481/', '[1] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[6] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin Pharmacol 56 (2000): 693-8[7] Pillai U, Muzaffar J, Sandeep S, Yancey A "Grapefruit juice and verapamil: a toxic cocktail." South Med J 102 (2009): 308-9[8] McAllister RG, Jr "Clinical pharmacology of slow channel blocking agents." Prog Cardiovasc Dis 25 (1982): 83-102[9] "Product Information. Covera-HS (verapamil)." Searle, Skokie, IL.[10] Fuhr U, Muller-Peltzer H, Kern R, et al. "Effects of grapefruit juice and smoking on verapamil concentrations in steady state." Eur J Clin Pharmacol 58 (2002): 45-53[11] Arayne MS, Sultana N, Bibi Z "Review: grape fruit juice - drug interactions." Pak J Pharm Sci 18 (2005): 45-57[12] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions. 1998." Br J Clin Pharmacol 58 (2004): S831-40; discussion S841-3[13] Zaidenstein R, Dishi V, Gips M, Soback S, Cohen N, Weissgarten J, Blatt A, Golik A "The effect of grapefruit juice on the pharmacokinetics of orally administered verapamil." Eur J Clin Pharmacol 54 (1998): 337-40', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61396, 'Ceritinib', 'Tizanidine', 'Major', 'Ceritinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if ceritinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if ceritinib is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting ceritinib therapy and periodically during treatment.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61482/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobio