INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61404, 'Ceritinib', 'Torasemide', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of torsemide. The interaction has not been evaluated in pharmacokinetic studies; however, torsemide has been shown to undergo metabolism by CYP450 2C9.', NULL, 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors.', NULL, 'Caution is advised when torsemide is prescribed with CYP450 2C9 inhibitors. Patients should be monitored for potentially increased adverse effects such as dehydration, hypotension, electrolyte imbalances, hyperglycemia, tinnitus, and hearing loss.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61490/', '[1] Vormfelde SV, Engelhardt S, Zirk A, et al. "CYP2C9 polymorphisms and the interindividual variability in pharmacokinetics and pharmacodynamics of the loop diuretic drug torsemide." Clin Pharmacol Ther 76 (2004): 557-66[2] Miners JO, Rees DLP, Valente L, Veronese ME, Birkett DJ "Human hepatic cytochrome p450 2c9 catalyzes the rate-limiting pathway of torsemide metabolism." J Pharmacol Exp Ther 272 (1995): 1076-81[3] "Product Information. Demedex (torsemide)." Boehringer Mannheim, Gaithersburg, MD.[4] Miners JO, Birkett DJ "Cytochrome P4502C9: an enzyme of major importance in human drug metabolism." Br J Clin Pharmacol 45 (1998): 525-38[5] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61405, 'Ceritinib', 'Trabectedin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of trabectedin with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of trabectedin during and for 2 weeks after treatment with itraconazole. If a potent CYP450 3A4 inhibitor is required for short-term use (i.e., less than 14 days), it is advisable to administer the CYP450 3A4 inhibitor one week after the trabectedin infusion and discontinue it the day prior to the next infusion. Close monitoring for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy is recommended during long-term coadministration, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61491/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Machiels JP, Staddon A, Herremans C, et al. "Impact of cytochrome P450 3A4 inducer and inhibitor on the pharmacokinetics of trabectedin in patients with advanced malignancies: open-label, multicenter studies." Cancer Chemother Pharmacol 74 (2014): 729-37[5] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[8] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
