INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61421, 'Ceritinib', 'Ulipristal', 'Moderate', 'Based on in vitro inhibition data, coadministration with ulipristal may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ulipristal.', NULL, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ulipristal is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61507/', '[1] "Product Information. ella (ulipristal)." Afaxys Inc., Charleston, SC.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61422, 'Ceritinib', 'Upadacitinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of upadacitinib, which is primarily metabolized by the isoenzyme. Side effects including lymphopenia, neutropenia, anemia, serious infections, and hyperlipidemia may be increased.', NULL, 'Caution is recommended if upadacitinib is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is recommended if upadacitinib is used with potent CYP450 3A4 inhibitors. Pharmacologic effects of upadacitinib should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61508/', '[1] "Product Information. Rinvoq (upadacitinib)." AbbVie US LLC, North Chicago, IL.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61423, 'Ceritinib', 'Valbenazine', 'Major', 'Coadministration with a strong CYP450 3A4 inhibitor may increase the systemic exposure and risk of adverse effects of valbenazine, including somnolence and QT interval prolongation. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity. Increased concentrations of valbenazine and its active metabolite, (+)-alfa-dihydrotetrabenazine, may lead to clinically significant prolongation of the QT interval. In addition, central nervous system-depressant effects may be additively or synergistically increased in patients taking valbenazine with certain other drugs that cause these effects, especially in elderly or debilitated patients.', NULL, 'Pharmacologic response to valbenazine should be monitored more closely whenever a strong inhibitor of CYP450 3A4 (e.', NULL, 'Pharmacologic response to valbenazine should be monitored more closely whenever a strong inhibitor of CYP450 3A4 (e.g., itraconazole, ketoconazole, clarithromycin) is added to or withdrawn from therapy. Valbenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. Assessment of baseline QT interval and periodic monitoring during therapy may be considered. The manufacturer recommends reducing the dose of valbenazine to 40 mg once daily during concomitant administration with strong CYP450 3A4 inhibitors. Valbenazine dosage may be increased accordingly if these agents are discontinued. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. When valbenazine is used in combination with other drugs that cause CNS depression, patients should be monitored for potentially excessive or prolonged CNS depression. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61509/', '[1] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[2] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (61424, 'Ceritinib', 'Valproic acid', 'Moderate', 'Coadministration with ceritinib may increase the plasma concentration and the risk of adverse effects of drugs that are substrates of CYP450 2A6 and/or 2E1. The proposed mechanism, based on in vitro data, is decreased clearance due to ceritinib-mediated inhibition of CYP450 2A6 and 2E1.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of CYP450 2A6 and/or 2E1, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ceritinib is used concomitantly with drugs that are substrates of CYP450 2A6 and/or 2E1, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ceritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61510/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[4] "Product Information. Zykadia (ceritinib)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharma