INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360864, 'Cyclophosphamide', 'Azathioprine', 'Moderate', 'Concomitant administration of azathioprine and cyclophosphamide may cause hepatotoxicity, in some cases associated with hepatocellular necrosis. The mechanism is unknown. In addition, the risk of bone marrow suppression may be increased when azathioprine and cyclophosphamide are coadministered.', 'DDInter', 1767317475, 'If concomitant therapy is needed patients should be monitored for signs and symptoms of hepatotoxicity and myelotoxicity. Patients should be advised to promptly notify their physician if they experience fever, chills, sore throat, mouth sores, unusual fatigue, bleeding, bruising, abdominal pain, darkened urine, or pale stools.', 'Synergism', 'If concomitant therapy is needed patients should be monitored for signs and symptoms of hepatotoxicity and myelotoxicity.', NULL, NULL, 'Moderate', 'pharmacodynamic');
