INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62119, 'Chloramphenicol', 'Butabarbital', 'Moderate', 'Barbiturates may increase the hepatic metabolism of chloramphenicol. Serum chloramphenicol levels and effectiveness may be decreased. Coadministration also may enhance the pharmacologic effect of barbiturates. The proposed mechanism is induction of chloramphenicol metabolism by barbiturates and inhibition of barbiturate metabolism by chloramphenicol.', NULL, 'The effectiveness of chloramphenicol should be monitored, and plasma levels should be checked as clinically appropriate.', NULL, 'The effectiveness of chloramphenicol should be monitored, and plasma levels should be checked as clinically appropriate. Dosage should be increased as needed. The patient also should be monitored for barbiturate toxicity.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62205/', '[1] Bloxham RA, Durbin GM, Johnson T, Winterborn MH "Chloramphenicol and phenobarbitone--a drug interaction." Arch Dis Child 54 (1979): 76-7[2] Koup JR, Gibaldi M, McNamara P, et al "Interaction of chloramphenicol with phenytoin and phenobarbital." Clin Pharmacol Ther 24 (1978): 571-5[3] Kearns GL, Bocchini JA, Brown RD, et al "Absence of a pharmacokinetic interaction between chloramphenicol and acetaminophen in children." J Pediatr 107 (1985): 134-9[4] Krasinski K, Kusmiesz H, Nelson JD "Pharmacologic interactions among chloramphenicol, phenytoin and phenobarbital." Pediatr Infect Dis 1 (1982): 232-5[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Ambrose PJ "Clinical pharmacokinetics of chloramphenicol and chloramphenicol succinate." Clin Pharmacokinet 9 (1984): 222-38[7] Smith AL, Weber A "Pharmacology of chloramphenicol." Pediatr Clin North Am 30 (1983): 209-36[8] Palmer DL, Despopoulos A, Rael ED "Induction of chloramphenicol metabolism by phenobarbital." Antimicrob Agents Chemother 1 (1972): 112-5[9] Mulhall A, de Louvois J, Hurley R "The pharmacokinetics of chloramphenicol in the neonate and young infant." J Antimicrob Chemother 12 (1983): 629-39', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62120, 'Chloramphenicol', 'Carbenicillin', 'Moderate', 'Chloramphenicol may inhibit the bactericidal effect of penicillins when used to treat bacterial meningitis and staphylococcal endocarditis. Chloramphenicol acts by inhibiting cellular protein synthesis and can change actively growing bacteria into static ones. Since penicillins work by inhibiting cell wall synthesis, their effectiveness may be reduced. In addition, neurologic toxicity may be enhanced.', NULL, 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis.', NULL, 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis. If the combination is necessary, some experts recommend administering the penicillin several hours before the chloramphenicol. Patients should be closely monitored for altered antimicrobial efficacy.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62206/', '[1] Shalit I, Marks MI "Chloramphenicol in the 1980s." Drugs 28 (1984): 281-91[2] Wallace JF, Smith RH, Garcia M, Petersdorf RG "Studies on the pathogenesis of meningitis. VI: antagonism between penicillin and chloramphenicol in experimental pneumococcal meningitis." J Lab Clin Med 70 (1967): 408-18[3] Jawetz E "The use of combinations of antimicrobial drugs." Annu Rev Pharmacol 8 (1968): 151[4] Garrod L "Causes of failure in antibiotic treatment." Br Med J 4 (1972): 441', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62121, 'Iron', 'Chloramphenicol', 'Moderate', 'Chloramphenicol can cause bone marrow depression and inhibit red blood cell maturation, which may interfere with the therapeutic effects of iron or vitamin B12 in the treatment of anemia.', NULL, 'Patients with preexisting anemia should preferably not receive chloramphenicol due to the drug''s depressive effect on bone marrow and reticulocytes.', NULL, 'Patients with preexisting anemia should preferably not receive chloramphenicol due to the drug''s depressive effect on bone marrow and reticulocytes. If use is unavoidable, the lowest effective dosage of chloramphenicol should be given. Hematologic response to iron or vitamin B12 therapy should be closely monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62207/', '[1] Scott JL, Finegold SY, Belkin GA, Lawrence JS "A controlled double-blind study of the hematologic toxicity of chloramphenicol." N Engl J Med 272 (1965): 1137-42[2] Haile CA "Chloramphenicol toxicity." South Med J 70 (1977): 479-80[3] Saidi P, Wallerstein RO, Aggeler PM "Effect of chloramphenicol on erythropoiesis." J Lab Clin Med 57 (1961): 247-56', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62122, 'Chloramphenicol', 'Cefaclor', 'Moderate', 'Limited, primarily in vitro, data suggest that chloramphenicol may antagonize the bactericidal activity of cephalosporins against certain clinical isolates of gram-negative rods, group B streptococci, and Staphylococcus aureus. This antagonism appears to occur against strains for which chloramphenicol is bacteriostatic, and has been demonstrated with cefoperazone, cefotaxime, and ceftriaxone. The proposed mechanism is inhibition of protein synthesis by chloramphenicol, resulting in less protein substrate for cephalosporins to act on as inhibitors of bacterial cell wall synthesis.', NULL, 'The manufacturers recommend to avoid concomitant use.', NULL, 'The manufacturers recommend to avoid concomitant use . However, if concurrent administration cannot be avoided, the possibility of antagonism should be considered, and patients should be monitored for altered therapeutic effect.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62208/', '[1] Brown TH, Alford RH "Antagonism by chloramphenicol of broad-spectrum beta-lactam antibiotics against Klebsiella pneumoniae." Antimicrob Agents Chemother 25 (1984): 405-7[2] Asmar BI, Prainito M, Dajani AS "Antagonistic effect of chloramphenicol in combination with cefotaxime or ceftriaxone." Antimicrob Agents Chemother 32 (1988): 1375-8[3] Agencia Española de Medicamentos y Productos Sanitarios Healthcare "Centro de información online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] Brown TH, Alford RH "Failure of chloramphenicol and ce