INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62133, 'Chlorpropamide', 'Chloramphenicol', 'Moderate', 'The concomitant use of chloramphenicol and oral sulfonylureas may result in an enhanced hypoglycemic effect. The proposed mechanism is reduced hepatic clearance of sulfonylureas, or displacement of sulfonylureas from protein binding sites.', NULL, 'Patients should be monitored for hypoglycemia when chloramphenicol is added to a sulfonylurea regimen, and for hyperglycemia when chloramphenicol is discontinued.', NULL, 'Patients should be monitored for hypoglycemia when chloramphenicol is added to a sulfonylurea regimen, and for hyperglycemia when chloramphenicol is discontinued. The sulfonylurea dosage may require adjustment in affected patients. Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs.', 'Metabolism, Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62219/', '[1] Petitpierre B, Fabre J "Chlorpropamide and chloramphenicol." Lancet 1 (1970): 789[2] Brunova E, Slabochova Z, Platilova H, Pavlik F, Grafnetterova J, Dvoracek K "Interaction of tolbutamide and chloramphenicol in diabetic patients." Int J Clin Pharmacol 15 (1977): 7-12[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5