INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62144, 'Clozapine', 'Chloramphenicol', 'Major', 'Coadministration of clozapine with other agents that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', NULL, 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function.', NULL, 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function. Agents that may be significantly myelotoxic include antineoplastic drugs, some anticonvulsant and antirheumatic medications, albendazole, chloramphenicol, colchicine, dapsone, interferons, linezolid, pentamidine, procainamide, and zidovudine.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62230/', '[1] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] Warrington SJ, Ankie