INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62277, 'Chloramphenicol', 'Lurbinectedin', 'Major', 'Coadministration with a moderate CYP450 3A4 inhibitor is predicted to increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity. Concomitant use of lurbinectedin and moderate CYP450 3A4 inhibitors should generally be avoided. If the coadministration of lurbinectedin with a moderate CYP450 3A4 inhibitor cannot be avoided, a dose reduction for lurbinectedin may be considered.', NULL, 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy.', NULL, 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy. Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62363/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62278, 'Chloramphenicol', 'Lutetium Lu 177 dotatate', 'Moderate', 'Coadministration of chloramphenicol with other agents that can cause bone marrow depression or aplastic anemia may increase the risk and/or severity of chloramphenicol hematologic toxicity. Serious and fatal blood dyscrasias (aplastic anemia, hypoplastic anemia, thrombocytopenia and granulocytopenia) have been reported after the administration of chloramphenicol.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62364/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Sim SM, Back DJ, Breckenridge AM "The effect of various drugs on the glucuronidation of zidovudine (azidothymidine; AZT) by human liver microsomes." Br J Clin Pharmacol 32 (1991): 17-21[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Burger DM, Meenhorst PL, Koks CH, Beijnen JH "Drug interactions with zidovudine." AIDS 7 (1993): 445-60[5] "Product Information. Chloromycetin (chloramphenicol)." Parke-Davis, Morris Plains, NJ.[6] Cerner Multum, Inc "ANVISA Bulário Eletrônico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[7] Agencia Española de Medicamentos y Productos Sanitarios Healthcare "Centro de información online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[8] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62279, 'Chloramphenicol', 'Macitentan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of macitentan, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4.', NULL, 'Caution is advisable if macitentan is used with inhibitors of CYP450 3A4. The product labeling recommends avoiding concomitant use with potent inhibitors (e.g., protease inhibitors, clarithromycin, cobicistat, conivaptan, delavirdine, itraconazole, ketoconazole, nefazodone, posaconazole, voriconazole), but no suggestions are given regarding use with less potent inhibitors. It may be reasonable to monitor therapeutic response to macitentan more closely.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62365/', '[1] "Product Information. Opsumit (macitentan)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62280, 'Chloramphenicol', 'Maraviroc', 'Moderate', 'Coadministration with inhibitors of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may increase the plasma concentrations of maraviroc, which is a substrate of both.', NULL, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.', NULL, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein. Dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62366/', '[1] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
