INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62284, 'Chloramphenicol', 'Methylphenobarbital', 'Moderate', 'Barbiturates may increase the hepatic metabolism of chloramphenicol. Serum chloramphenicol levels and effectiveness may be decreased. Coadministration also may enhance the pharmacologic effect of barbiturates. The proposed mechanism is induction of chloramphenicol metabolism by barbiturates and inhibition of barbiturate metabolism by chloramphenicol.', NULL, 'The effectiveness of chloramphenicol should be monitored, and plasma levels should be checked as clinically appropriate.', NULL, 'The effectiveness of chloramphenicol should be monitored, and plasma levels should be checked as clinically appropriate. Dosage should be increased as needed. The patient also should be monitored for barbiturate toxicity.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62370/', '[1] Bloxham RA, Durbin GM, Johnson T, Winterborn MH "Chloramphenicol and phenobarbitone--a drug interaction." Arch Dis Child 54 (1979): 76-7[2] Koup JR, Gibaldi M, McNamara P, et al "Interaction of chloramphenicol with phenytoin and phenobarbital." Clin Pharmacol Ther 24 (1978): 571-5[3] Kearns GL, Bocchini JA, Brown RD, et al "Absence of a pharmacokinetic interaction between chloramphenicol and acetaminophen in children." J Pediatr 107 (1985): 134-9[4] Krasinski K, Kusmiesz H, Nelson JD "Pharmacologic interactions among chloramphenicol, phenytoin and phenobarbital." Pediatr Infect Dis 1 (1982): 232-5[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Ambrose PJ "Clinical pharmacokinetics of chloramphenicol and chloramphenicol succinate." Clin Pharmacoki