INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62293, 'Chloramphenicol', 'Mezlocillin', 'Moderate', 'Chloramphenicol may inhibit the bactericidal effect of penicillins when used to treat bacterial meningitis and staphylococcal endocarditis. Chloramphenicol acts by inhibiting cellular protein synthesis and can change actively growing bacteria into static ones. Since penicillins work by inhibiting cell wall synthesis, their effectiveness may be reduced. In addition, neurologic toxicity may be enhanced.', NULL, 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis.', NULL, 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis. If the combination is necessary, some experts recommend administering the penicillin several hours before the chloramphenicol. Patients should be closely monitored for altered antimicrobial efficacy.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62379/', '[1] Shalit I, Marks MI "Chloramphenicol in the 1980s." Drugs 28 (1984): 281-91[2] Wallace JF, Smith RH, Garcia M, Petersdorf RG "Studies on the pathogenesis of meningitis. VI: antagonism between penicillin and chloramphenicol in experimental pneumococcal meningitis." J Lab Clin Med 70 (1967): 408-18[3] Ja