INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62307, 'Chloramphenicol', 'Nelarabine', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62393/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62308, 'Chloramphenicol', 'Neratinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', NULL, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62394/', '[1] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[6] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[7] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62309, 'Chloramphenicol', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the plasma concentrations of nilotinib, which is a substrate of both the isoenzyme and the efflux transporter. Data are not available for nilotinib in combination with other CYP450 3A4 or P-glycoprotein inhibitors. Theoretically, a reverse interaction may also occur, since many CYP450 3A4 and P-glycoprotein inhibitors are also substrates, and nilotinib is an inhibitor of both.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors. Pharmacologic response to nilotinib should be monitored more closely whenever a CYP450 3A4 or P-glycoprotein inhibitor is added to or withdrawn from therapy, and the nilotinib dosage adjusted as necessary. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Patients should also be monitored for altered efficacy and safety of the concomitant administered drug.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62395/', '[1] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
