INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62316, 'Chloramphenicol', 'Olaparib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme. Increased exposure to olaparib may increase the risk of adverse effects such as hematologic toxicity, nausea, vomiting, diarrhea, anorexia, dyspepsia, and abdominal pain or discomfort.', NULL, 'Caution is advised during concomitant use of olaparib with CYP450 3A4 inhibitors.', NULL, 'Caution is advised during concomitant use of olaparib with CYP450 3A4 inhibitors. A dosage reduction may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62402/', '[1] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
