INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62384, 'Chloramphenicol', 'Sodium ferric gluconate complex', 'Moderate', 'Chloramphenicol can cause bone marrow depression and inhibit red blood cell maturation, which may interfere with the therapeutic effects of iron or vitamin B12 in the treatment of anemia.', NULL, 'Patients with preexisting anemia should preferably not receive chloramphenicol due to the drug''s depressive effect on bone marrow and reticulocytes.', NULL, 'Patients with preexisting anemia should preferably not receive chloramphenicol due to the drug''s depressive effect on bone marrow and reticulocytes. If use is unavoidable, the lowest effective dosage of chloramphenicol should be given. Hematologic response to iron or vitamin B12 therapy should be closely monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62470/', '[1] Scott JL, Finegold SY, Belkin GA, Lawrence JS "A controlled double-blind study of the hematologic toxicity of chloramphenicol." N Engl J Med 272 (1965): 1137-42[2] Haile CA "Chloramphenicol toxicity." South Med J 70 (1977): 479-80[3] Saidi P, Wallerstein RO, Aggeler PM "Effect of chloramphenicol on erythropoiesis." J Lab Clin Med 57 (1961): 247-56', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62385, 'Chloramphenicol', 'Sodium phosphate, monobasic (p32)', 'Moderate', 'The concomitant use of bone marrow depressants and sodium phosphate P-32 may have additive myelosuppressive effects.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with sodium phosphate P-32.', NULL, 'Patients should be monitored for excessive bone marrow suppression during treatment with sodium phosphate P-32. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62471/', '[1] AnazaoHealth Corporation "P32 Sodium Phosphate. Available from: URL: http://dailymed.nlm.nih.gov/dailymed/fda/fdaDrugXsl.cfm?setid=735f92e3-cc92-4d9b-afe8-f935a685ee78" ([2012 Jun]):', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62386, 'Chloramphenicol', 'Solifenacin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of solifenacin, which is primarily metabolized by the isoenzyme.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary. Patients should be advised to contact their physician if they experience undue adverse effects of solifenacin such as severe abdominal pain or constipation for 3 or more days. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62472/', '[1] "Product Information. VESIcare (solifenacin)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
