INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (62436, 'Chloramphenicol', 'Aflibercept', 'Moderate', 'Coadministration of chloramphenicol with other agents that can cause bone marrow depression or aplastic anemia may increase the risk and/or severity of chloramphenicol hematologic toxicity. Serious and fatal blood dyscrasias (aplastic anemia, hypoplastic anemia, thrombocytopenia and granulocytopenia) have been reported after the administration of chloramphenicol.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62522/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Sim SM, Back DJ, Breckenridge AM "The effect of various drugs on the glucuronidation of zidovudine (azidothymidine; AZT) by human liver microsomes." Br J Clin Pharmacol 32 (1991): 17-21[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Burger DM, Meenhorst PL, Koks CH, Beijnen JH "Drug interactions with zidovudine." AIDS 7 (1993): 445-60[5] "Product Information. Chloromycetin (chloramphenicol)." Parke-Davis, Morris Plains, NJ.[6] Cerner Multum, Inc "ANVISA Bulário Eletrônico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[7] Agencia Española de Medicamentos y Productos Sanitarios Healthcare "Centro de información online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[8] "Product Information. Lonsurf (tipiracil-trifluridine).