INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360950, 'Azathioprine', 'Meningococcal polysaccharide vaccine group C', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360951, 'Azathioprine', 'Mercaptopurine', 'Major', 'Azathioprine may potentiate the toxicities of 6-mercaptopurine. Azathioprine is metabolized to 6-mercaptopurine in vivo, thus concomitant administration may result in additive adverse effects, including myelosuppression, immunosuppression, and hepatotoxicity.', 'DDInter', 1767317475, 'Concurrent use of azathioprine and 6-mercaptopurine should be avoided.', 'Synergism', 'Concurrent use of azathioprine and 6-mercaptopurine should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360952, 'Azathioprine', 'Methotrexate', 'Minor', 'Methotrexate inhibits the hepatic metabolism of the thiopurines. Decreased first-pass metabolism and increased bioavailability result. The clinical significance of this interaction has not been determined and is probably insignificant with standard intermittent oral doses of methotrexate. Generally the combination has beneficial effects when used in the treatment of acute lymphoblastic leukemia.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360953, 'Azathioprine', 'Metocurine', 'Minor', 'Limited data suggest that azathioprine may variously enhance, reduce, or reverse the effects of neuromuscular blockers. The mechanism for enhancement of effects is unknown. The mechanism for reduction of effects may be related to inhibition of phosphodiesterase in motor nerve terminals. Routine dose titration and standard monitoring of the patient are recommended.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
