INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1360963, 'Azathioprine', 'Mycophenolate mofetil', 'Moderate', 'Mycophenolic acid and azathioprine both exert their immunosuppressant effect by inhibiting purine metabolism. Coadministration may increase the risk and severity of hematologic toxicity due to additive pharmacodynamic effects.', 'DDInter', 1767317475, 'Due to their similar mechanism of action, concomitant administration of mycophenolic acid and azathioprine is not recommended.', 'Metabolism', 'Due to their similar mechanism of action, concomitant administration of mycophenolic acid and azathioprine is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
